#include <iostream>
#include <bits/stdc++.h>
#include <algorithm>

using namespace std;
int commonChild(string s1, string s2)
{

    int len_s1 = s1.length();
    int len_s2 = s2.length();

    vector<vector<int>> matrix(len_s2 + 1, vector<int>(len_s1 + 1));

    for (int i = 0; i < len_s2; i++)
    {

        for (int j = 0; j < len_s1; j++)
        {

            if (s1[j] == s2[i])
            {

                matrix[i + 1][j + 1] = matrix[i][j] + 1;
            }

            else
            {

                matrix[i + 1][j + 1] = max(max(matrix[i][j], matrix[i][j + 1]), matrix[i + 1][j]);
            }
        }
    }

    return matrix.back().back(); 
}

int main()
{
    string s1 = "";
    string s2 = "";

    //cout << s1.length() << endl;
    int i = 1;
    int j = 10;
    int k = 100;

    //cout << max(max(i,j),k);

    //cout << s1[1] << " " << s1[2] << " " << s1[3];
    cout << commonChild(s1,s2);
}

